/*******************************************************************************
 * Declaração de funções
*******************************************************************************/
void MAvg(int *, int *, int *);

/*******************************************************************************
 * MAvg
*******************************************************************************/
void MAvg(register int *A, register int *B, register int *C){
    /* A keyword register indica ao compilador para alocar as variáveis em registos.
     * Sugestão: Comece por pensar o que é o i e j (em termos da cache)!
    */
    register int i, j, aux = 0;
    
    for (i = 0; i < N; i++){
        for (j = 0; j < N; j++){
            C[i*N + j] = A[i*N + j] + B[i*N + j];
            C[i*N + j] = C[i*N + j]/2;
        }
    }
}


